    def turnOnWhite(self):
            shell_cmd = 'python3 /Users/zhuguangyi/Assignment/DDD428IDS/proc.py'
            cmd = shlex.split(shell_cmd)
            p = subprocess.Popen(cmd, shell=False, stdout=subprocess.PIPE, stderr=subprocess.STDOUT)
            #for i in range(10):
            count=0
            while count<20 and p.poll() is None:
                        line = p.stdout.readline()
                        line = line.strip()
                        if line:
                                # print('{}'.format(str(line, encoding='utf-8')))
                                procstr = str(line, encoding='utf-8')
                                count+=1
                                self.listProc.addItem(procstr)

