class IP_RulesThread(QtCore.QThread):#线程2：加载和维护过滤规则
        signal_ip_rules=QtCore.pyqtSignal(str)
        def __init__(self):
                super(IP_RulesThread,self).__init__()

        def ip_rules(self):  # 黑名单ip检测
                print('in rules!')
                global temp
                global black_ips
                global signal_cap
                global ip_alarm
                global ip_alarm_toggle
                global alarm_count
                count = 0
                while True:
                        if len(temp) == 0:  # 缓存池空：等待
                                count = 0
                                continue
                        if len(temp) == maxsize and signal_cap == True :  # 缓存池满，并且捕获器还在抓包：清除缓存池（看网速，可能严重丢包）
                                count = 0
                                temp = []
                                signal_cap = False

                        while count < len(temp):
                                pattern = re.compile(r'(?<![\.\d])(?:\d{1,3}\.){3}\d{1,3}(?![\.\d])')
                                ips = pattern.findall(str(temp[count]))
                                if ips == [] or len(ips)<2:  # 没匹配到
                                        #print('没有匹配')
                                        continue
                                for i in range(len(black_ips)):
                                        if black_ips[i][0][:black_ips[i][1]] == self.deal_ip(ips[0])[0][:black_ips[i][1]] or \
                                                black_ips[i][0][:black_ips[i][1]] == self.deal_ip(ips[1])[0][
                                                                                     :black_ips[i][1]]:
                                                #Ui_MainWindow.PushNotification(self)
                                                ip_alarm_toggle=True
                                                #print('抓到了:'+temp[count])
                                                if allow_notification == True:
                                                        os.system(
                                                                """osascript -e 'display notification "请前往警报页面查看详情" with title "DDD428IDS" sound name "Glass" subtitle "紧急情况：规则被触发！" '""")

                                                alarm_count+=4

                                                ip_alarm=temp[count]
                                ip_alarm_toggle=False

                                count += 1
                                print(count)
                        else:
                                continue
                                signal_cap = True  # 这可能会导致认为关闭捕获器后捕获器自启动的情况。。待讨论。

        def deal_ip(self,ip):  # 为了适应规则中带的无分类子网掩码，需要将IP转成二进制字符串进行逐位匹配，ip_len是前缀长度（匹配长度）
                ip = ip.replace('\n', '')
                if ip.find('/') == -1:
                        ip += '/32'
                ip, ip_len = ip.split('/')
                ip = ip.split('.')
                ip_bin = ''
                for i in range(4):
                        ip_bin += bin(int(ip[i]))[2:].zfill(8)
                return [ip_bin, int(ip_len)]

        def update_black_ips(self):  # 更新黑名单ip列表，读取文件，重写black_ips
                global black_ips
                snort_rules = open("./snort3-community.txt", 'r')
                ips = open('/Users/zhuguangyi/Assignment/DDD428IDS/emerging-Block-IPs.txt').readlines()
                pattern = re.compile(r'(?<![\.\d])(?:\d{1,3}\.){3}\d{1,3}(?![\.\d])')
                for i in range(len(ips)):
                        ip = pattern.findall(ips[i])
                        if ip != []:
                                black_ips += [self.deal_ip(ips[i])]



        def run(self):

            global signal_ip
            while True:
                if signal_ip:
                        self.update_black_ips()
                        self.ip_rules()
                        print("IP规则有效")
                else:
                        continue


